        Task&lt;Void&gt; task = activityRecognitionClient.requestActivityUpdates(2000, getActivityRecognitionPendingIntent());

        task.addOnSuccessListener(
                success -&gt; Log.d(&quot;ActivityRecognition&quot;, &quot;Start activity recognition!&quot;)
