            System.out.println(isBalanced(&quot;{{}(&quot;) ? &quot;YES&quot; : &quot;NO&quot;); // NO 
            System.out.println(isBalanced(&quot;{()}&quot;) ? &quot;YES&quot; : &quot;NO&quot;); // YES 
