    			if(closeBracket.containsKey(ch)) {
    				// node close bracket				
    				if(node == null) {
    					return false;
    				}
    				if(!(node.nodeElement).equals(closeBracket.get(ch))) {
    					return false;
    				}
    				node = node.parent;	
    			} else {
    				//make node for open bracket				
    				 node = new Node(ch, node);
    			}
    		}		
    		
    		if(node != null) {
    			return false;
    		}
    		
    		return true;
    	}
    }
    
    
