        float b;
    public:
        void f1();
        Cls():a(0), b(0.0) {};
        friend std::ostream &amp;operator&lt;&lt;(std::ostream &amp;os, const Cls &amp;cls) {
            return os &lt;&lt; cls.a &lt;&lt; &quot; &quot; &lt;&lt; cls.b &lt;&lt; std::endl;
        };
};

int main()
{
    Cls obj;
    std::cout &lt;&lt; obj;
}
```

