    @Repository
    public class GenericRepository {

    @PersistenceContext
    private EntityManager entityManager;

    public List&lt;?&gt; findAll(String tableName) {
        return entityManager.createQuery(&quot;SELECT e FROM &quot; + tableName + &quot; e&quot;).getResultList();
    }

    public Object findById(String tableName, Long id) {
        return entityManager.find(tableName, id);
    }

    public void save(String tableName, Object entity) {
        entityManager.merge(entity);
    }

    public List&lt;?&gt; findByProperty(String tableName, String property, String value) {
        String query = &quot;SELECT e FROM &quot; + tableName + &quot; e WHERE e.&quot; + property + &quot; = :value&quot;;
        TypedQuery&lt;?&gt; typedQuery = entityManager.createQuery(query, Object.class);
        typedQuery.setParameter(&quot;value&quot;, value);
        return typedQuery.getResultList();
    }

