		@Mock
		RestTemplate restTemplate;
		@InjectMocks
        @Spy
		Service service;
		ResponseEntity responseEntity = mock(ResponseEntity.class);

		@Test
		public void test() throws Exception {
			Mockito.when(restTemplate.getForEntity(
					Mockito.anyString(),
					ArgumentMatchers.any(Class.class)
					))
