class Solution {
    public int searchRange(int[] nums, int target, boolean findStart) {
        int ans = -1;
        int start = 0;
        int end = nums.length - 1;
        while (start &lt;= end) {
            int mid = start + (end - start) / 2;
            if (target &lt; nums[mid]) {
                end = mid - 1;
            } else if (target &gt; nums[mid]) {
                start = mid + 1;
            } else {
                ans = mid;
                if (findStart) {
                    end = mid - 1;
                } else {
                    start = mid + 1;
                }
