    &lt;w:pict&gt;
     &lt;v:rect style=&quot;position:absolute;...&quot;&gt;
      &lt;v:textbox style=&quot;...;layout-flow:vertical;mso-layout-flow-alt:bottom-to-top;...&quot;&gt;
       &lt;w:txbxContent&gt;
        ...
