public class WithMockJwtSecurityContextFactory implements WithSecurityContextFactory&lt;WithMockJwt&gt; {
    @Override
    public SecurityContext createSecurityContext(WithMockJwt annotation) {
        val jwt = Jwt.withTokenValue(&quot;token&quot;)
