// No reason to be public
private static String ResultCount(WebDriver driver) {
	String[] t = driver.findElement(By.cssSelector(&quot;.srp-controls__count-heading&quot;)).getText().split(&quot; &quot;);
	String resultCount = t[0] + &quot; Results Found&quot;;
	return resultCount;
