      sum += min;
      index = ques.indexOf(min);
    
      if (index == 0 &amp;&amp; l &gt; 2) {
        ques.remove(index);
        ques.remove(index + 1);
        l -= 2;
      } else if (index == 0 &amp;&amp; l == 2) {
        ques.remove(index);
