        if (ActivityCompat.checkSelfPermission(this, Manifest.permission.ACCESS_FINE_LOCATION) != PackageManager.PERMISSION_GRANTED &amp;&amp; ActivityCompat.checkSelfPermission(this, Manifest.permission.ACCESS_COARSE_LOCATION) != PackageManager.PERMISSION_GRANTED) {
            Log.d(TAG, &quot;getCurrentLocation: Inside If&quot;);
            return;
        }
        Log.d(TAG, &quot;getCurrentLocation: 02&quot;);
        LocationServices.getFusedLocationProviderClient(this).requestLocationUpdates(locationRequest, new LocationCallback() {
            @Override
            public void onLocationResult(LocationResult locationResult) {
                super.onLocationResult(locationResult);
                Log.d(TAG, &quot;getCurrentLocation: Inside Result &quot;+locationResult);
                LocationServices.getFusedLocationProviderClient(getApplicationContext()).removeLocationUpdates(this);
                if (locationResult != null &amp;&amp; locationResult.getLocations().size() &gt; 0) {
                    int latestLocationIndex = locationResult.getLocations().size() - 1;
                    double latitude = locationResult.getLocations().get(latestLocationIndex).getLatitude();
                    double longitude = locationResult.getLocations().get(latestLocationIndex).getLongitude();
                    Log.d(TAG, &quot;getCurrentLocation: Latitude  &quot;+latitude);
                    Log.d(TAG, &quot;getCurrentLocation: Longitude &quot;+longitude);
                }
            }
        }, Looper.getMainLooper());
    }

