@Slf4j
public class LogsInterceptor implements HandlerInterceptor {

    private static final ThreadLocal&lt;StopWatch&gt; STOPWATCH = new ThreadLocal&lt;&gt;();
    protected static final String REQUEST_ID = &quot;requestId&quot;;
    protected static final String REQUEST_URI = &quot;requestUri&quot;;
    protected static final String REQUEST_METHOD = &quot;requestMethod&quot;;
    protected static final String RESPONSE_TIME_MS = &quot;responseTimeMs&quot;;
    protected static final String RESPONSE_STATUS_CODE = &quot;responseStatusCode&quot;;

    @Override
    public boolean preHandle(HttpServletRequest httpRequest, HttpServletResponse response, Object handler) {
        try {
            StopWatch sw = new StopWatch();
            STOPWATCH.set(sw);
            STOPWATCH.get().start();

            String requestId = UUID.randomUUID().toString();
            MDC.put(REQUEST_ID, requestId);
            MDC.put(REQUEST_METHOD, httpRequest.getMethod());
            MDC.put(REQUEST_URI, httpRequest.getRequestURI());
            return true;
        } catch (Exception exception) {
            STOPWATCH.remove();
            return false;
        }
    }

    @Override
    public void afterCompletion(HttpServletRequest request, HttpServletResponse response, Object handler, @Nullable Exception ex) {
        try {
            STOPWATCH.get().stop();
            long timeInMillis = STOPWATCH.get().getTotalTimeMillis();
            MDC.put(RESPONSE_TIME_MS, String.valueOf(timeInMillis));
            MDC.put(RESPONSE_STATUS_CODE, String.valueOf(response.getStatus()));
            log.info(&quot;Request: {} {} Time taken: {}ms Status: {}&quot;, request.getMethod(), request.getRequestURI(), timeInMillis, response.getStatus());
        } finally {
            MDC.clear();
            STOPWATCH.remove();
            log.debug(&quot;afterCompletion called&quot;);
        }
    }

    protected void startStopWatch() {
        STOPWATCH.set(new StopWatch());
    }
}
```

