          .thenCompose(timestamp -&gt; {
            calltimes.put(id, timestamp); 
            return call;
          });
 }

 void finished() {
  callTimes.put(END, Instant.now());
 }

 Map&lt;Call, Instant&gt; callTimes() {
  return Collections.unmodifiableMap(callTimes);
 }
}
```

