		public void insertToTree(T v) {
			if (value == null) {
				value = v;
				return;
			}
			if (v.compareTo(value) &lt; 0) {
				if (left == null) {
					left = new Node&lt;T&gt;();
				}
				left.insertToTree(v);
			} else {
				if (right == null) {
					right = new Node&lt;T&gt;();
				}
				right.insertToTree(v);
			}
		}
		
