            int offset = depthToUse - levelToUse-1;
            offset = (int)(Math.pow(offset,Math.log(depthToUse)*1.4));
            offset = Math.max(offset,3);
    
    
            PrintableNodeInfo leftChild = node.getLeftChildInfo();
