    public class ProgressSpinner extends JComponent implements ActionListener {
    
    	private static final long serialVersionUID = 1L;
    
    	private Dimension size;
    	private int actualSize;
    	private int halfSize;
    	private int thickness;
    
    	private double angle;
    	private double extent;
    
    	private double angleDelta = -1;
    	private double extentDelta = -5;
    
    	private boolean flip = false;
    	private boolean running = false;
    
    	private Timer timer;
    
    	public ProgressSpinner(int size) {
    		this(size, 2);
    	}
    
    	public ProgressSpinner(int size, int thickness) {
    		this.actualSize = size;
    		this.halfSize = this.actualSize / 2;
    
    		size += thickness;
    		this.size = new Dimension(size, size);
    		this.thickness = thickness;
    
    		this.timer = new Timer(10, this);
    	}
    
    	public void startAnimation() {
    		if (!this.running) {
    			this.timer.start();
    			this.running = true;
    		}
    	}
    
    	public void stopAnimation() {
    		if (this.running) {
    			this.timer.stop();
    			this.running = false;
    			super.repaint();
    		}
    	}
    
    	@Override
    	public Dimension getPreferredSize() {
    		return this.size;
    	}
    
    	protected void paintComponent(Graphics g) {
    		super.paintComponent(g);
    
    		if (!this.running)
    			return;
    
    		Graphics2D g2d = (Graphics2D) g.create();
    		g2d.setRenderingHint(RenderingHints.KEY_ALPHA_INTERPOLATION, RenderingHints.VALUE_ALPHA_INTERPOLATION_QUALITY);
    		g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);
