    		g2d.setRenderingHint(RenderingHints.KEY_INTERPOLATION, RenderingHints.VALUE_INTERPOLATION_BILINEAR);
    		g2d.setRenderingHint(RenderingHints.KEY_RENDERING, RenderingHints.VALUE_RENDER_QUALITY);
    		g2d.setRenderingHint(RenderingHints.KEY_STROKE_CONTROL, RenderingHints.VALUE_STROKE_PURE);
    
            // Center the animation in case a
            // layout manager resizes this component.
    		Rectangle bounds = g2d.getClipBounds();
    		int x = (bounds.width / 2) - this.halfSize;
    		int y = (bounds.height / 2) - this.halfSize;
    
    		Arc2D.Double arc = new Arc2D.Double(x, y, this.actualSize, this.actualSize, angle, extent, Arc2D.OPEN);
    		BasicStroke stroke = new BasicStroke(this.thickness, BasicStroke.CAP_BUTT, BasicStroke.JOIN_ROUND);
    		g2d.setStroke(stroke);
    		g2d.draw(arc);
    		g2d.dispose();
    	}
    
    	@Override
    	public void actionPerformed(ActionEvent e) {
    		this.angle += this.angleDelta;
    		this.extent += this.extentDelta;
    		if (Math.abs(this.extent) % 360D == 0) {
    			this.angle = this.angle - this.extent;
    			this.flip = !this.flip;
    			extent = this.flip ? 360D : 0D;
    		}
    		super.repaint();
    	}
    
    }

