    List&lt;TestPOJO&gt; setNormalizedValue(List&lt;TestPOJO&gt; pojos) {
        int i = 0;
        final double[][] rawValue = new double[5][pojos.size()];
        for (TestPOJO pojo: pojos) {
            for (var entry : FUNCTIONS_MAP.entrySet()) {
                final int position = entry.getKey();
                final var getter = entry.getValue().getRight();
                // you can pass the pojo to Function.apply
                rawValue[position][i] = getter.apply(pojo);
            }
            i++;
        }
        final Pair&lt;double[], double[]&gt; stdPopAndMean = getStdPopAndMean(rawValue);
        final double[] stdPop = stdPopAndMean.getLeft();
        final double[] mean = stdPopAndMean.getRight();

        return pojos.stream()
                .map(p -&gt; {
                            TestPOJO.TestPOJOBuilder builder = p.toBuilder();
                            for (var entry : FUNCTIONS_MAP.entrySet()) {
                                final int position = entry.getKey();
                                final var getter = entry.getValue().getRight();
                                final var setter = entry.getValue().getLeft();
                                final double similarity = getNormalizedValue(getter.apply(p), stdPop[position], mean[position]);

                                // a BiConsumer takes 2 arguments, allowing you to pass the builder to it
                                setter.accept(builder, similarity);
                            }
                            return builder.build();

                        }
                )
                .collect(Collectors.toList());
