        private static &lt;A,B&gt; scala.collection.immutable.Map&lt;A, B&gt; toScalaMap(java.util.Map&lt;A, B&gt; m) {
          List&lt;Tuple2&lt;A,B&gt;&gt; javaTuples = javaMap.entrySet().stream()
                    .map(x -&gt; Tuple2.apply(x.getKey(), x.getValue()))
                    .collect(Collectors.toList());
        
          scala.collection.Map scalaMap = scala.collection.Map$.MODULE$.apply(JavaConversions.asScalaBuffer(javaTuples).toSeq());
          return scalaMap;
    }

