    int[] arr = new int[]{10, 20, 20, 10, 10, 30, 50, 10, 20};

    HashSet&lt;Integer&gt; unmatched = new HashSet&lt;&gt;();
    int pairs = 0;
    for(int i = 0; i &lt; arr.length; i++) {
        if(!unmatched.add(arr[i])) {
            unmatched.remove(arr[i]);
            pairs++;
        }
    }


