	static int sockMerchant(int n, int[] ar) {

		int counter = 0;
		int count = 0;
		//
		for (int i = 0; i &lt; ar.length; i++) {
			count = 1;
			//
			for (int j = i + 1; j &lt; ar.length; j++) {
				if (ar[i] == ar[j]) {
					count++;
				}
			}
			
			if (count % 2 == 0) {
				counter++;
			}
		}

		return counter;
	}

