				&lt;groupId&gt;org.apache.maven.plugins&lt;/groupId&gt;
				&lt;artifactId&gt;maven-shade-plugin&lt;/artifactId&gt;
				&lt;executions&gt;
					&lt;execution&gt;
						&lt;phase&gt;package&lt;/phase&gt;
						&lt;goals&gt;
							&lt;goal&gt;shade&lt;/goal&gt;
						&lt;/goals&gt;
						&lt;configuration&gt;
							&lt;transformers&gt;
								&lt;transformer implementation=&quot;org.apache.maven.plugins.shade.resource.ManifestResourceTransformer&quot;&gt;
									&lt;manifestEntries&gt;
										&lt;Main-Class&gt;io.vertx.core.Starter&lt;/Main-Class&gt;
										&lt;Main-Verticle&gt;main.Main&lt;/Main-Verticle&gt;
									&lt;/manifestEntries&gt;
								&lt;/transformer&gt;
							&lt;/transformers&gt;
							&lt;filters&gt;
								&lt;filter&gt;
									&lt;artifact&gt;*:*&lt;/artifact&gt;
									&lt;excludes&gt;
										&lt;exclude&gt;META-INF/*.SF&lt;/exclude&gt;
										&lt;exclude&gt;META-INF/*.DSA&lt;/exclude&gt;
										&lt;exclude&gt;META-INF/*.RSA&lt;/exclude&gt;
									&lt;/excludes&gt;
								&lt;/filter&gt;
								&lt;filter&gt;
									&lt;artifact&gt;org.sonatype.plexus:*&lt;/artifact&gt;
									&lt;excludes&gt;
										&lt;exclude&gt;META-INF/plexus/components.xml&lt;/exclude&gt;
										&lt;exclude&gt;META-INF/sisu/javax.inject.Named&lt;/exclude&gt;
									&lt;/excludes&gt;
								&lt;/filter&gt;
								&lt;filter&gt;
									&lt;artifact&gt;org.apache.maven:*&lt;/artifact&gt;
									&lt;excludes&gt;
										&lt;exclude&gt;META-INF/plexus/components.xml&lt;/exclude&gt;
										&lt;exclude&gt;META-INF/sisu/javax.inject.Named&lt;/exclude&gt;
									&lt;/excludes&gt;
