        double bb = jd + b + 1524;  
        double cc = Math.floor((bb - 122.1) / 365.25);  
        double dd = Math.floor(365.25 * cc);  
        double ee = Math.floor((bb - dd) / 30.6001);  
        day = (bb - dd) - Math.floor(30.6001 * ee);  
        month = ee - 1;  
        if (ee &gt; 13) {  
