    public class AmazonS3RateFriendlyProxy implements InvocationHandler {
    
        private static final int MAX_RATE = 100_000;
        private static final int MIN_RATE = 100;
        private static final int MAX_RETRY_COUNT = 300;
        private static final Logger logger = LoggerFactory.getLogger(AmazonS3RateFriendlyProxy.class);
    
        public static final AmazonS3 rateFriendlyProxy(AmazonS3 s3) {
            return (AmazonS3) newProxyInstance(
                    s3.getClass().getClassLoader(),
                    new Class[] { AmazonS3.class },
                    new AmazonS3RateFriendlyProxy(s3));
        }
    
        private AmazonS3 s3;
        private ReentrantLock lock = new ReentrantLock(true);
        private AtomicLong leapTime = new AtomicLong();
        private AtomicInteger rate = new AtomicInteger(MAX_RATE);
    
        private AmazonS3RateFriendlyProxy(AmazonS3 s3) {
            this.s3 = s3;
        }
    
        @Override
        public Object invoke(Object proxy, Method method, Object[] args) throws Throwable {
            for (int retryCount = 0;; retryCount++) {
                int rateSnapshot = rate.get();
                limitRate(rateSnapshot, leapTime, lock);
                try {
    
                    return method.invoke(s3, args);
    
                } catch (Exception e) {
                    String msg = getRootCauseMessage(e);
                    if (!containsAny(msg, &quot;Please reduce your request rate&quot;, &quot;Please try again&quot;) || retryCount &gt; MAX_RETRY_COUNT)
                        throw e;
                    logger.warn(&quot;[dashboard] Rejected retry {}, at rate {}, {}&quot;, retryCount, rateSnapshot, msg);
                    rate.compareAndSet(rateSnapshot, max(rateSnapshot / 2, MIN_RATE));
                    sleep(SECONDS.toMillis(1));
                } finally {
                    rate.compareAndSet(rateSnapshot, min(rateSnapshot + 1, MAX_RATE));
                }
            }
        }

        public static void limitRate(int rate, AtomicLong leapTime, ReentrantLock rateLock) {
            long targetLeapTime = 1_000_000_000 / rate;
            rateLock.lock();
