@Component
public class MessageHandler {

	private final Retry handlerRetry;
	private final ScheduledExecutorService retryScheduler;

	public MessageHandler(Retry handlerRetry, ScheduledExecutorService retryScheduler) {
		this.handlerRetry = handlerRetry;
		this.retryScheduler = retryScheduler;
	}

	public ListenableFuture&lt;String&gt; process(Message&lt;String&gt; inputMessage) {
		String input = inputMessage.getPayload();
		AtomicLong retryCounter = new AtomicLong(0L);
		CompletableFuture&lt;String&gt; retryingFuture = handlerRetry
            .executeCompletionStage(retryScheduler, () -&gt; doWork(input, retryCounter))
            .toCompletableFuture();
		return new CompletableToListenableFutureAdapter&lt;&gt;(retryingFuture);
	}

	private CompletableFuture&lt;String&gt; doWork(String input, AtomicLong retryCounter) {
		return CompletableFuture.supplyAsync(() -&gt; {
			try {
				long attempt = retryCounter.getAndIncrement();
				if (attempt == 0) {
					System.out.println(&quot;Processing: {}&quot;, input);
				} else {
					System.out.println(&quot;Processing: {}, retry-attempt: {}&quot;, input, attempt);
				}
				if (input.equals(&quot;Input:0&quot;)) {
