        materialToolbar=v.findViewById(R.id.materialHomeToolbar);
        materialToolbar.inflateMenu(R.menu.topapp_menu);

        materialToolbar.setOnMenuItemClickListener(new Toolbar.OnMenuItemClickListener()
        {
