package com.example.client;

import com.example.client.response.PrometheusResponse;
import org.eclipse.microprofile.rest.client.annotation.RegisterClientHeaders;
import org.eclipse.microprofile.rest.client.annotation.RegisterProvider;
import org.eclipse.microprofile.rest.client.inject.RegisterRestClient;
import org.jboss.resteasy.annotations.jaxrs.QueryParam;

import javax.inject.Singleton;
import javax.ws.rs.Consumes;
import javax.ws.rs.GET;
import javax.ws.rs.Path;
import javax.ws.rs.Produces;

@Singleton
@Path(&quot;/api/v1/query&quot;)
@RegisterRestClient(configKey = &quot;prometheus-client&quot;)
@RegisterClientHeaders(RequestInternalServiceHeaderFactory.class)
@RegisterProvider(RequestInternalServiceFilter.class)
public interface MetricClient {
    @GET
    @Path(&quot;query&quot;)
    @Consumes(&quot;application/json&quot;)
    @Produces(&quot;application/json&quot;)
    PrometheusResponse getTpsSummary(@QueryParam String query);
}
