        configurer.favorPathExtension(true)
                .mediaType(&quot;json&quot;, MediaType.APPLICATION_JSON)
                .mediaType(&quot;xml&quot;, MediaType.APPLICATION_XML)
                .mediaType(&quot;com&quot;, MediaType.APPLICATION_JSON);
    }

