        int left = (width - cropSize) / 2;
        int top = (height - cropSize) / 2;
        Bitmap croppedImage = Bitmap.createBitmap(image, left, top, cropSize, cropSize);
        croppedImage.setHasAlpha(true);
        return croppedImage;
    }

    private Bitmap adjustContrast(Bitmap image, float value) {
        Bitmap adjustedImage = Bitmap.createBitmap(image.getWidth(), image.getHeight(), image.getConfig());
        Canvas canvas = new Canvas(adjustedImage);

        ColorMatrix colorMatrix = new ColorMatrix();
        colorMatrix.set(new float[]{
                value / 100f, 0, 0, 0, 0,
                0, value / 100f, 0, 0, 0,
                0, 0, value / 100f, 0, 0,
                0, 0, 0, 1, 0
        });

        Paint paint = new Paint();
        paint.setColorFilter(new ColorMatrixColorFilter(colorMatrix));
        canvas.drawBitmap(image, 0, 0, paint);

        return adjustedImage;
    }

    private Bitmap adjustHue(Bitmap image, float value) {
        Bitmap adjustedImage = Bitmap.createBitmap(image.getWidth(), image.getHeight(), image.getConfig());
        Canvas canvas = new Canvas(adjustedImage);

        ColorMatrix colorMatrix = new ColorMatrix();
        colorMatrix.setRotate(0, value);
        colorMatrix.setRotate(1, value);
        colorMatrix.setRotate(2, value);

        Paint paint = new Paint();
        paint.setColorFilter(new ColorMatrixColorFilter(colorMatrix));
        canvas.drawBitmap(image, 0, 0, paint);

        return adjustedImage;
    }

    private Bitmap adjustSaturation(Bitmap image, float value) {
        Bitmap adjustedImage = Bitmap.createBitmap(image.getWidth(), image.getHeight(), image.getConfig());
        Canvas canvas = new Canvas(adjustedImage);

        ColorMatrix colorMatrix = new ColorMatrix();
        colorMatrix.setSaturation(value / 100f);

        Paint paint = new Paint();
        paint.setColorFilter(new ColorMatrixColorFilter(colorMatrix));
        canvas.drawBitmap(image, 0, 0, paint);

