    	private static String compute(long number) {
    		if(number &gt; 0 &amp;&amp;  number % 2 == 0)
    			return &quot;No&quot;;
    		long sqrt = floorSqrt(number);
    		for(long i = 3; i&lt;sqrt;i+=2) {
    			if(number % i == 0)
    				return &quot;No&quot;;
    		}
    		return &quot;Yes&quot;;
    	}
    
    	private static long floorSqrt(long number) {
    		if(number == 0 || number == 1)
    			return number;
    		long start = 0;
    		long end = number;
    		long answer = 0;
    		while (start &lt;= end) {
    			long mid = (start + end)/2;
    			if(mid*mid == number)
    				return mid;
    			if(mid*mid &lt; number) {
    				start = mid+1;
    				answer = mid;
    			}else {
    				end = mid-1;
    			}
    		}
    		return answer;
    	}
    
    }

