    public void clear() {
    
        // Disconnect all links between nodes
    
        LinkedListNode current = head;
    
        while (current != null) {
    
            LinkedListNode next = current.next;
    
            current.next = null;
    
            current = next;
    
        }
    
        
    
        // Set head to null
    
        head = null;
    
    }

**Circular Linked List:**

    public void clear() {
    
        if (head == null) {
    
            return; // Empty list
    
        }
    
        // Disconnect all links between nodes
    
        LinkedListNode current = head;
    
        do {
    
            LinkedListNode next = current.next;
    
            current.next = null;
    
            current = next;
    
        } while (current != head);
    
        
    
        // Set head to null
    
        head = null;
    
    }

**Doubly Linked List:**

    public void clear() {
    
        // Disconnect all links between nodes
    
        LinkedListNode current = head;
    
        while (current != null) {
    
            LinkedListNode next = current.next;
    
            // For doubly linked lists
    
            current.prev = null;
    
            current.next = null;
    
            current = next;
    
        }
    
        
    
        // Set head and tail to null
    
        head = null;
    
        // For doubly linked lists
    
        tail = null;
    
    }

**Circular Doubly Linked List:**

    public void clear() {
