                return;
            }
    
            TarArchiveEntry entry = new TarArchiveEntry(sourceFile);
            tarOut.putArchiveEntry(entry);
            if (sourceFile.isFile()) {
                writeFile(new BufferedInputStream(new FileInputStream(sourceFile)), tarOut);
                tarOut.closeArchiveEntry();
            } else {
