```
class BaseUnitConverter&lt;T&gt; {

	private InstantiatorUnitConverter&lt;T&gt; me;

	protected final &lt;M, E&gt; M storeValue(M t, double value, E tt) {
		this.me = new InstantiatorUnitConverter&lt;E&gt;(value, tt, st(tt));
