                            timeToNotify = &quot;12 : 0&quot; + materialTimePicker.getMinute() + &quot; &quot; + formatter.format(time);
                            binding.timeEditText.setText(timeToNotify);
                        } else {
                            timeToNotify = &quot;12 : &quot; + materialTimePicker.getMinute() + &quot; &quot; + formatter.format(time);
                            binding.timeEditText.setText(timeToNotify);
                        }
                    }
                } else {
                    if (materialTimePicker.getHour() &gt; 0 &amp;&amp; materialTimePicker.getHour() &lt;= 9) {
                        if (materialTimePicker.getMinute() &lt;= 9) {
                            timeToNotify = &quot;0&quot; + materialTimePicker.getHour() + &quot; : 0&quot; + materialTimePicker.getMinute() + &quot; &quot; + formatter.format(time);
                            binding.timeEditText.setText(timeToNotify);
                        } else {
                            timeToNotify = &quot;0&quot; + materialTimePicker.getHour() + &quot; : &quot; + materialTimePicker.getMinute() + &quot; &quot; + formatter.format(time);
                            binding.timeEditText.setText(timeToNotify);
                        }
                    } else if (materialTimePicker.getHour() &gt; 9) {
                        if (materialTimePicker.getMinute() &lt;= 9) {
                            timeToNotify = materialTimePicker.getHour() + &quot; : 0&quot; + materialTimePicker.getMinute() + &quot; &quot; + formatter.format(time);
                            binding.timeEditText.setText(timeToNotify);
                        } else {
                            timeToNotify = materialTimePicker.getHour() + &quot; : &quot; + materialTimePicker.getMinute() + &quot; &quot; + formatter.format(time);
                            binding.timeEditText.setText(timeToNotify);
                        }
                    } else if (materialTimePicker.getHour() == 0) {
                        if (materialTimePicker.getMinute() &lt;= 9) {
                            timeToNotify = &quot;12 : 0&quot; + materialTimePicker.getMinute() + &quot; &quot; + formatter.format(time);
                            binding.timeEditText.setText(timeToNotify);
                        } else {
                            timeToNotify = &quot;12 : &quot; + materialTimePicker.getMinute() + &quot; &quot; + formatter.format(time);
                            binding.timeEditText.setText(timeToNotify);
                        }
                    }
                }
            });
        }
    
        private void showMaterialDatePickerToUpdate() {
            MaterialDatePicker.Builder &lt; Long &gt; datePicker = MaterialDatePicker.Builder.datePicker();
            datePicker.setTitleText(&quot;Select Date...&quot;);
            datePicker.setSelection(MaterialDatePicker.todayInUtcMilliseconds());
            datePicker.setInputMode(MaterialDatePicker.INPUT_MODE_CALENDAR);
            MaterialDatePicker &lt; Long &gt; picker = datePicker.build();
            picker.show(getSupportFragmentManager(), &quot;MATERIAL_DATE_PICKER&quot;);
            picker.addOnPositiveButtonClickListener(selection - &gt; {
                dateToNotify = picker.getHeaderText();
                binding.dateEditText.setText(dateToNotify);
            });
        }
    }
    
