    public void pauseMusic() {
        if (mediaPlayer.isPlaying()) {
            mediaPlayer.pause();
            currentPosition = mediaPlayer.getCurrentPosition();
            isPlaying = false;
            mediaSession.setActive(false);
            showNotification();

            if (callback != null) {
                callback.onPauseSong();
            }
        }
        handler.removeCallbacksAndMessages(null);
    }

    public void resumeMusic() {
        if (!mediaPlayer.isPlaying()) {
            mediaPlayer.seekTo(currentPosition);
            mediaPlayer.start();
            isPlaying = true;
            mediaSession.setActive(true);
            showNotification();

            if (callback != null) {
                callback.onResumeSong();
            }
        }
        updateNotificationProgressPeriodically();
    }

    private void playPreviousSong() {
        if (callback != null) {
            callback.onPreviousSong();
        }
    }

    public void playNextSong() {
        if (callback != null) {
            callback.onNextSong();
        }
    }

    @SuppressLint(&quot;RestrictedApi&quot;)
    public void startMusic(String fileName, String songName) {
        if (fileName != null) {
            isPlaying = true;
            currentSongName = songName;
            Log.d(TAG, &quot;startMusic: &quot; + currentSongName);
            int resourceId = getResources().getIdentifier(fileName, &quot;raw&quot;, getPackageName());
            Uri uri = Uri.parse(&quot;android.resource://&quot; + getPackageName() + &quot;/&quot; + resourceId);

            try {
                mediaPlayer.reset();
                mediaPlayer.setDataSource(getApplicationContext(), uri);
                mediaPlayer.prepareAsync();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void seekToPosition(int position) {
        if (mediaPlayer.isPlaying()) {
            mediaPlayer.seekTo(position);
            showNotification();
        }
