        if (callback != null) {
            callback.onPrepared(duration);
        }
        mediaPlayer.start();
        showNotification();
        updateNotificationProgressPeriodically();

        if (currentPosition &gt; 0) {
            mediaPlayer.seekTo(currentPosition);
            currentPosition = 0; // Reset the saved position
        }
    }

    private void updateNotificationProgressPeriodically() {
        // Create a new Runnable that will update the progress and post it with a delay of 1 second
        Runnable progressUpdateRunnable = new Runnable() {
            @Override
            public void run() {
                // Update the progress of the SeekBar in the notification
                showNotification();

                // Post the same Runnable again after a delay of 1 second
                handler.postDelayed(this, 1000);
            }
        };

        // Start the periodic progress update
        handler.post(progressUpdateRunnable);
    }

 
 private void showNotification() {
     // Create the notification channel (if needed)
     createNotificationChannel();

     int progress = mediaPlayer.getCurrentPosition();
     int max = mediaPlayer.getDuration();

     if (callback != null) {
         callback.onProgress(progress, max);
     }

     // Create the remote views for the custom layout
     RemoteViews contentView = new RemoteViews(getPackageName(), R.layout.custom_notification_layout);

     contentView.setTextViewText(R.id.subtitleTextView, currentSongName);

