     // Set the playback buttons
     contentView.setOnClickPendingIntent(R.id.notification_previousButton, createPendingIntent(ACTION_PREVIOUS));

     String action = isPlaying ? ACTION_PAUSE : ACTION_RESUME;
     int playPauseIconResId = isPlaying ? R.drawable.baseline_pause_24 : R.drawable.baseline_play_arrow_24;

     contentView.setOnClickPendingIntent(R.id.notification_playPauseButton, createPendingIntent(action));
     contentView.setImageViewResource(R.id.notification_playPauseButton, playPauseIconResId);
     contentView.setInt(R.id.notification_playPauseButton, &quot;setImageResource&quot;, playPauseIconResId);

     contentView.setOnClickPendingIntent(R.id.notification_nextButton, createPendingIntent(ACTION_NEXT));

     // Create the pending intent for the notification
     Intent intent = new Intent(this, MainActivity.class);
     PendingIntent pendingIntent = PendingIntent.getActivity(this, 0, intent, PendingIntent.FLAG_IMMUTABLE);

     // Create the notification builder
     NotificationCompat.Builder builder = new NotificationCompat.Builder(this, CHANNEL_ID)
             .setSmallIcon(R.drawable.ic_music_note)
             .setContentIntent(pendingIntent)
             .setPriority(NotificationCompat.PRIORITY_DEFAULT)
             .setShowWhen(false)
             .setVisibility(NotificationCompat.VISIBILITY_PUBLIC)
             .setCustomContentView(contentView)
             .setOngoing(true); // Set the notification as ongoing (cannot be dismissed by the user)

     // Set the large icon
     Bitmap largeIcon = BitmapFactory.decodeResource(getResources(), R.drawable.ic_music_icon);
     builder.setLargeIcon(largeIcon);

     // Update the notification
     notificationManager.notify(NOTIFICATION_ID, builder.build());
 }


    private PendingIntent createPendingIntent(String action) {
        Intent intent = new Intent(this, BackgroundMusicService.class);
        intent.setAction(action);

        if (Build.VERSION.SDK_INT &gt;= Build.VERSION_CODES.S) {
            return PendingIntent.getService(this, 0, intent, PendingIntent.FLAG_IMMUTABLE);
        } else {
            return PendingIntent.getService(this, 0, intent, PendingIntent.FLAG_UPDATE_CURRENT);
        }
    }



    private void createNotificationChannel() {
        if (Build.VERSION.SDK_INT &gt;= Build.VERSION_CODES.O) {
            CharSequence name = &quot;Background Music Channel&quot;;
            String description = &quot;Channel for background music playback&quot;;
            int importance = NotificationManager.IMPORTANCE_DEFAULT;
            NotificationChannel channel = new NotificationChannel(CHANNEL_ID, name, importance);
            channel.setDescription(description);
            notificationManager.createNotificationChannel(channel);
        }
    }



