        }

        static &lt;E extends Extension&lt;T&gt;, T&gt; E create(Class&lt;E&gt; type, T instance) {
            if (type.isInterface()) {
                ExtensionHandler&lt;T&gt; handler = new ExtensionHandler&lt;T&gt;(instance);
                List&lt;Class&lt;?&gt;&gt; interfaces = new ArrayList&lt;Class&lt;?&gt;&gt;();
