#[export_name = &quot;Java_com_eveheeero_example_TestClass_myFunc2&quot;]
fn myFunc&lt;&#39;local&gt;(mut env: JNIEnv&lt;&#39;local&gt;, class: JClass&lt;&#39;local&gt;, arg1: JClass&lt;&#39;local&gt;) -&gt; jobject /* return type must be () or jobject or jint or ... that all char is lowercase in jni */ {
