  env.call_method(&amp;class, &quot;exampleMethod2&quot;, &quot;(I)V&quot; /* (Arg)Return */, &amp;[JValueGen::Int(1)]).unwrap(); // if you want to object pointer, you can use into_raw() method after unwrap()
