Following is the above approach coded more dynamic. 

It uses the used range of the sheet from first row to last column. Values in last column are array items. All previous columns contain key values for objects, either objects or arrays, mapping following column values to that key.

    import org.apache.poi.ss.usermodel.*;
    import org.apache.poi.ss.util.CellRangeAddress;
    import java.io.FileInputStream;
    
    import org.json.JSONObject;
    
    class ReadExcelToJSONmultipleLevels {
        
     static CellRangeAddress getUsedRange(Sheet sheet) {
      int firstRow = sheet.getFirstRowNum();
      if (firstRow == -1) return null;
      int lastRow = sheet.getLastRowNum();
      if (lastRow == -1) return null;
      int firstCol = Integer.MAX_VALUE;
      int lastCol = -1;
      for (int r = firstRow; r &lt;= lastRow; r++) {
       Row row = sheet.getRow(r);
       if (row != null) {
        int thisRowFirstCol = row.getFirstCellNum();
        int thisRowLastCol = row.getLastCellNum()-1; // see API doc Row.getLastCellNum : Gets the index of the last cell contained in this row PLUS ONE.
        if (thisRowFirstCol &lt; firstCol) firstCol = thisRowFirstCol;
        if (thisRowLastCol &gt; lastCol) lastCol = thisRowLastCol;
       }   
      }
      if (firstCol == Integer.MAX_VALUE) return null;
      if (lastCol == -1) return null;
      return new CellRangeAddress(firstRow, lastRow, firstCol, lastCol);
     }  
    
     public static void main(String[] args) throws Exception {
    
      Workbook workbook = WorkbookFactory.create(new FileInputStream(&quot;./ExcelExample.xlsx&quot;));
      DataFormatter dataFormatter = new DataFormatter(new java.util.Locale(&quot;en&quot;, &quot;US&quot;));
      // from 5.2.0 on the DataFormatter can set to use cached values for formula cells
      dataFormatter.setUseCachedValuesForFormulaCells(true);
      
      Sheet sheet = workbook.getSheetAt(0);
      Cell cell;
      String cellValue;
    
      CellRangeAddress usedRange = getUsedRange(sheet);
      
      System.out.println(usedRange); 
      
      int firstRowNum = usedRange.getFirstRow();
      int firstCellNum = usedRange.getFirstColumn();
      
      int levelCount = 	usedRange.getLastColumn() - usedRange.getFirstColumn() + 1;
      
      if (levelCount &lt; 2) return;
    
      StringBuilder stringBuilder = new StringBuilder();
      
      boolean[] isLevelOpen = new boolean[levelCount];
      for (int i = 0; i &lt; levelCount; i++) isLevelOpen[i] = false;
      
      stringBuilder.append(&quot;{\n&quot;);
        
      for (Row row : sheet) {
       for (int level = 0; level &lt; levelCount; level++) {
        cell = row.getCell(firstCellNum + level);
        cellValue = dataFormatter.formatCellValue(cell);
        if (!&quot;&quot;.equals(cellValue)) { //level found
         // if previous levels are open, close them
         for (int i = levelCount-1; i &gt;= level; i--) {
          if (isLevelOpen[i] &amp;&amp; i != level) {
           if (i==levelCount-1) stringBuilder.append(&quot;\n&quot;); //last level
           else if (i==levelCount-2) stringBuilder.append(&quot;]\n&quot;); //penultimate level
           else stringBuilder.append(&quot;}\n&quot;); //other previous levels
           isLevelOpen[i] = false;
          }
          // if same level is open, close it followed by comma, because a new one get opened now  
          if (isLevelOpen[i] &amp;&amp; i == level) {
           if (i==levelCount-1) stringBuilder.append(&quot;,\n&quot;); //last level
           else if (i==levelCount-2) stringBuilder.append(&quot;],\n&quot;); //penultimate level
           else stringBuilder.append(&quot;},\n&quot;); //other previous levels
           isLevelOpen[i] = false;
          }
         }    
    
         String startTag = &quot;&quot;;
         if (level == levelCount-1) startTag = &quot;&quot;;
         else if (level == levelCount-2) startTag = &quot;: [\n&quot;;
         else startTag = &quot;: {\n&quot;;
         stringBuilder.append(&quot; \&quot;&quot; + cellValue + &quot;\&quot;&quot; + startTag);
         isLevelOpen[level] = true;
        }
       }
      }
      
      // if previous levels are open, close them
      for (int i = levelCount-1; i &gt;= 0; i--) {
       String endTag = &quot;&quot;;
       if (i == levelCount-1) endTag = &quot;\n&quot;;
       else if (i == levelCount-2) endTag = &quot;]\n&quot;;
       else endTag = &quot;}\n&quot;;
       if (isLevelOpen[i]) {
        stringBuilder.append(endTag);
        isLevelOpen[i] = false;
       }  
      }
      
      stringBuilder.append(&quot;}\n&quot;);
     
      String jsonText = stringBuilder.toString();
      System.out.println(jsonText);
      
      JSONObject jsonObject= new JSONObject(jsonText);
      System.out.println(jsonObject.toString(1));
    
      workbook.close();
     }
    }

