            }

            // I get the preferred size. Since I want the size to be
            // the same as the parent, so I return that same.
            @Override
            public Dimension preferredLayoutSize(Container parent) {
                int width = Integer.MIN_VALUE;
                int height = Integer.MIN_VALUE;
                for (Component child : parent.getComponents()) {
                    width = Integer.max(width, child.getPreferredSize().width);
                    height = Integer.max(height, child.getPreferredSize().height);
                }
                Insets insets = parent.getInsets();
                return new Dimension(width + insets.left + insets.right, height + insets.top + insets.top);
            }

            // The minimum size is the same as the preferred one
            @Override
            public Dimension minimumLayoutSize(Container parent) {
                int width = Integer.MIN_VALUE;
                int height = Integer.MIN_VALUE;
                for (Component child : parent.getComponents()) {
                    width = Integer.max(width, child.getMinimumSize().width);
                    height = Integer.max(height, child.getMinimumSize().height);
                }
                Insets insets = parent.getInsets();
                return new Dimension(width + insets.left + insets.right, height + insets.top + insets.top);
            }

            // I give the same size and position to all the components.
            @Override
            public void layoutContainer(Container parent) {
                for (Component comp : parent.getComponents()) {
                    comp.setBounds(0, 0, parent.getWidth(), parent.getHeight());
                }
            }
        }
    }


