package com.instacloneapi.instacloneapi.config;

import io.jsonwebtoken.Jwts;
import io.jsonwebtoken.security.Keys;
import jakarta.servlet.FilterChain;
import jakarta.servlet.ServletException;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import org.springframework.lang.NonNull;
import org.springframework.security.core.Authentication;
import org.springframework.security.core.GrantedAuthority;
import org.springframework.security.core.context.SecurityContextHolder;
import org.springframework.web.filter.OncePerRequestFilter;

import javax.crypto.SecretKey;
import java.io.IOException;
import java.util.Collection;
import java.util.Date;
import java.util.HashSet;
import java.util.Set;

public class JwtTokenGeneratorFilter extends OncePerRequestFilter {

    @Override
    protected void doFilterInternal(
            @NonNull HttpServletRequest request,
            @NonNull HttpServletResponse response,
            @NonNull FilterChain filterChain
    ) throws ServletException, IOException {
        Authentication authentication = SecurityContextHolder.getContext().getAuthentication();

        if (authentication != null) {
            SecretKey key = Keys.hmacShaKeyFor(SecurityContext.JWT_KEY.getBytes());
            String jwt = Jwts.builder()
                    .setIssuer(&quot;instagram&quot;)
                    .setIssuedAt(new Date())
                    .claim(&quot;authorities&quot;, populateAuthorities(authentication.getAuthorities()))
                    .claim(&quot;username&quot;, authentication.getName())
                    .setExpiration(new Date(new Date().getTime() + 1000000000))
                    .signWith(key).compact();
            response.setHeader(SecurityContext.HEADER, jwt);
        }
        filterChain.doFilter(request, response);
    }

    public static String populateAuthorities(Collection&lt;? extends GrantedAuthority&gt; collection) {
        Set&lt;String&gt; authorities = new HashSet&lt;&gt;();

        for (GrantedAuthority authority : collection) {
            authorities.add(authority.getAuthority());
        }
        return String.join(&quot;,&quot;, authorities);
    }

    protected boolean shouldNotFilter(HttpServletRequest request) {
        return !request.getServletPath().equals(&quot;/signin&quot;);
    }
}
