            Logger.getLogger(DebitInstallmentPaymentTimer.class.getName()).log(Level.INFO, &quot;ListenAddress is :&quot; + listenAddress.toString());
            if (listenPort.equals(&quot;9091&quot;) &amp;&amp; listenAddress.contains(&quot;eservice-n3.tamin.org&quot;)) {
                return true;
