```
public class Junk{
	Thread t;
	volatile boolean waiting = false;
    public Junk(){
		t = new Thread(){
		    @Override
		    public void run(){
				synchronized(this){
					waiting = true;
					try{
					    wait();
					}catch(Exception e){
					    e.printStackTrace();
					}
					System.out.println(&quot;finished&quot;);
				}
		    }
		};
		}
