    private static int extractJson(String input, int index, StringBuilder buf) {
        int bracketLevel = 0;
        int st = 0;
        while (index &lt; input.length()) {
            char c = input.charAt(index++);
            switch (st) {
                case 0:
                    switch (c) {
                        case &#39;{&#39;:
                            buf.append(c);
                            ++bracketLevel;
                            break;
                        case &#39;}&#39;:
                            buf.append(c);
                            --bracketLevel;
                            if (bracketLevel &lt;= 0) {
                                return index;
                            }
                            break;
                        case &#39;\&#39;&#39;:
                            buf.append(&#39;&quot;&#39;);
                            st = 1;
                            break;
                        default:
                            buf.append(c);
                            break;
                    }
                    break;
                case 1:
                    switch (c) {
                        case &#39;\&#39;&#39;:
                            buf.append(&#39;&quot;&#39;);
                            st = 0;
                            break;
                        case &#39;&quot;&#39;:
                            buf.append(&#39;\\&#39;);
                            buf.append(c);
                            break;
                         case &#39;\\&#39;:
                            st = 2;
                            break;
                        default:
                            buf.append(c);
                    }
                    break;
                case 2:
                    switch (c) {
                        case &#39;\&#39;&#39;:
                            buf.append(c);
                            st = 1;
                            break;
                        default:
                            buf.append(&#39;\\&#39;);
                            buf.append(c);
                            st = 1;
                            break;
                    }
                    break;
            }
        }
        return index;
    }

