                    jIndex = list.get(j);
                    r = str.charAt(list.get(j));
                    if (r == &#39;]&#39;) {
                        String substring = str.substring(lIndex, jIndex + 1);
                        if (isValidJSON(substring)) {
                            res.add(new Group(lIndex, jIndex));
                            i = j ;
