        List&lt;LogObject&gt; resObjece = Lists.newLinkedList();
        Integer index = 0;
        for (Group re : res) {
            if (re.left &gt; index) {
                resObjece.add(new LogObject(StrType.STRING, str.substring(index, re.left)));
            }
            resObjece.add(new LogObject(StrType.JSON, str.substring(re.left, re.right + 1)));
            index = re.right + 1;
        }
        if (index != size) {
            resObjece.add(new LogObject(StrType.STRING, str.substring(index, size)));
        }
        return resObjece;
