```cpp
%typemap(in) meters_t {
    // Code to convert from jdouble to meters_t
}

%typemap(out) meters_t {
    // Code to convert from meters_t to jdouble
}

// For the constructor issue
%ignore Foo::Foo(meters_t);
%extend Foo {
    Foo() {
        return new Foo(meters_t{0.0 * boost::units::si::meters});
